import React from 'react';
import Square from "./Components/Square";
import "./App.css";
import BoardRow from "./Components/BoardRow";
import {calculateWinner} from "./utils/util";
import Game from "./Components/Game";

const App = () => {

    // const [values, setValues] = React.useState(Array(9).fill(""));
    const [history, setHistory] = React.useState([Array(9).fill("")]);
    const [isXNext, setXNext] = React.useState(true);
    const [curMove, setCurMove] = React.useState(0);
    const currentSquares = history[curMove];
    const winner = calculateWinner(currentSquares);
    let status = "";
    if(winner){
        status = `Winner: ${winner}`;
    }else {
        status = `Next player: ${isXNext ? "X" : "O"}`;
    }


    const clickHandler = (e, posX, posY) => {
        if(currentSquares[3 * posX + posY] || calculateWinner(currentSquares)) return;
        setHistory((prevHistory) => {
            const nextSquares = currentSquares.slice();
            if(isXNext){
                nextSquares[3 * posX + posY] = "X";
            }else {
                nextSquares[3 * posX + posY] = "O";
            }
            return [...prevHistory, nextSquares];
        });
        setCurMove(history.length);
        setXNext(!isXNext);
    }

    const jumpTo = (nextMove) => {
        setCurMove(nextMove);
        setXNext(nextMove % 2 === 0);
    }

    return (
        <div className="app">
            <div className={"status"}>
                <label>{status}</label>
            </div>
            <div className={"app-game"}>
                <Game onPlay={clickHandler} history={history} curMove={curMove} onJump={jumpTo} />
            </div>
        </div>
    );
};

export default App;
