import React from 'react';
import BoardRow from "./BoardRow";
import "./Game.css";

const Game = (props) => {
    const currentSquares = props.history[props.history.length - 1];
    return (
        <div className="game">
            <div className="game-board">
                <div className="borderRow">
                    <BoardRow positionX={0} value={currentSquares.slice(0, 3)} onSquareClick={props.onPlay} />
                    <BoardRow positionX={1} value={currentSquares.slice(3, 6)} onSquareClick={props.onPlay} />
                    <BoardRow positionX={2} value={currentSquares.slice(6)} onSquareClick={props.onPlay} />
                </div>
            </div>
            <div className="game-info">
                <ol>{/*TODO*/}</ol>
            </div>
        </div>
    );
};

export default Game;
