import React from 'react';
import Square from "./Components/Square";
import "./App.css";
import BoardRow from "./Components/BoardRow";

const App = () => {

    const [values, setValues] = React.useState(Array(9).fill(""));

    const clickHandler = (e) => {
        console.log(e.target);
        setValues((prevValues) => [...prevValues, "X"]);
    }

    return (
        <div className="app">
            <BoardRow row={0} value={values.slice(0, 3)} onSquareClick={clickHandler} />
            <BoardRow row={1} value={values.slice(3, 6)} onSquareClick={clickHandler} />
            <BoardRow row={2} value={values.slice(6)} onSquareClick={clickHandler} />
        </div>
    );
};

export default App;
